import React, { useState, useEffect } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";

function AdminInventory() {
  const [medicines, setMedicines] = useState([]);
  const [pharmacies, setPharmacies] = useState([]);
  const [selectedPharmacy, setSelectedPharmacy] = useState("");
  const [newMedicine, setNewMedicine] = useState({ name: "", stock: 0 });
  const [editedMedicine, setEditedMedicine] = useState({});
  const [editModeId, setEditModeId] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    fetchMedicines();
    fetchPharmacies();

    const token = localStorage.getItem("token");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        if (decoded.email === "admin@gmail.com") {
          setIsAdmin(true);
        }
      } catch (err) {
        console.error("Invalid token", err);
      }
    }
  }, []);

  const fetchMedicines = () => {
    axios
      .get("http://localhost:5000/api/medicines/all")
      .then((res) => setMedicines(res.data))
      .catch((err) => console.error(err));
  };

  const fetchPharmacies = () => {
    axios
      .get("http://localhost:5000/api/pharmacies")
      .then((res) => setPharmacies(res.data))
      .catch((err) => console.error(err));
  };

  const handleAdd = () => {
    const selected = pharmacies.find((p) => p.name === selectedPharmacy);
    if (!selected) {
      alert("Please select a pharmacy");
      return;
    }

    const medicineWithPharmacy = {
      ...newMedicine,
      pharmacy_name: selected.name,
      latitude: selected.latitude,
      longitude: selected.longitude,
    };

    axios
      .post("http://localhost:5000/api/medicines/add", medicineWithPharmacy)
      .then(() => {
        fetchMedicines();
        setNewMedicine({ name: "", stock: 0 });
        setSelectedPharmacy("");
      })
      .catch((err) => console.error(err));
  };

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:5000/api/medicines/delete/${id}`)
      .then(() => fetchMedicines())
      .catch((err) => console.error(err));
  };

  const handleEditStart = (medicine) => {
    setEditModeId(medicine.id);
    setEditedMedicine({ name: medicine.name, stock: medicine.stock });
  };

  const handleEditCancel = () => {
    setEditModeId(null);
    setEditedMedicine({});
  };

  const handleUpdate = (id) => {
    axios
      .put(`http://localhost:5000/api/medicines/update/${id}`, editedMedicine)
      .then(() => {
        fetchMedicines();
        handleEditCancel();
      })
      .catch((err) => console.error(err));
  };

  if (!isAdmin) {
    return <p style={{ color: "red" }}>Access denied: Not an admin</p>;
  }

  return (
    <div className="container">
      <h2>Pharmacy Inventory (Admin)</h2>

      <h4>Add New Medicine</h4>
      <select
        value={selectedPharmacy}
        onChange={(e) => setSelectedPharmacy(e.target.value)}
      >
        <option value="">Select Pharmacy</option>
        {pharmacies.map((p) => (
          <option key={p.name} value={p.name}>
            {p.name}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Medicine Name"
        value={newMedicine.name}
        onChange={(e) =>
          setNewMedicine({ ...newMedicine, name: e.target.value })
        }
      />
      <input
        type="number"
        placeholder="Stock"
        value={newMedicine.stock}
        onChange={(e) =>
          setNewMedicine({ ...newMedicine, stock: e.target.value })
        }
      />
      <button onClick={handleAdd}>➕ Add Medicine</button>

      <h4>Current Stock</h4>
      <ul>
        {medicines.map((med) => (
          <li key={med.id}>
            {editModeId === med.id ? (
              <>
                <input
                  type="text"
                  value={editedMedicine.name}
                  onChange={(e) =>
                    setEditedMedicine({ ...editedMedicine, name: e.target.value })
                  }
                />
                <input
                  type="number"
                  value={editedMedicine.stock}
                  onChange={(e) =>
                    setEditedMedicine({
                      ...editedMedicine,
                      stock: e.target.value,
                    })
                  }
                />
                <button onClick={() => handleUpdate(med.id)}>✅ Save</button>
                <button onClick={handleEditCancel}>❌ Cancel</button>
              </>
            ) : (
              <>
                {med.name} ({med.stock}) - {med.pharmacy_name}
                <button onClick={() => handleEditStart(med)}>✏️ Edit</button>
                <button onClick={() => handleDelete(med.id)}>❌ Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminInventory;
